'use strict';

var cleanCID = require('../utils/clean-cid');

var v = require('is-ipfs');

var Stream = require('readable-stream');

var pump = require('pump');

module.exports = function (send) {
  return function (hash, opts) {
    opts = opts || {};
    var pt = new Stream.PassThrough();

    try {
      hash = cleanCID(hash);
    } catch (err) {
      if (!v.ipfsPath(hash)) {
        return pt.destroy(err);
      }
    }

    var query = {
      offset: opts.offset,
      length: opts.length
    };
    send({
      path: 'cat',
      args: hash,
      buffer: opts.buffer,
      qs: query
    }, function (err, stream) {
      if (err) {
        return pt.destroy(err);
      }

      pump(stream, pt);
    });
    return pt;
  };
};
