var promisify = require('promisify-es6');

var CID = require('cids');

var multihash = require('multihashes');

module.exports = function (send) {
  return promisify(function (args, opts, callback) {
    // TODO this needs to be adjusted with the new go-ipfs http-api
    if (args && CID.isCID(args)) {
      args = multihash.toB58String(args.multihash);
    }

    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    var request = {
      path: 'block/stat',
      args: args,
      qs: opts // Transform the response from { Key, Size } objects to { key, size } objects

    };

    var transform = function transform(stats, callback) {
      callback(null, {
        key: stats.Key,
        size: stats.Size
      });
    };

    send.andTransform(request, transform, callback);
  });
};
