'use strict';

var promisify = require('promisify-es6');

var transform = function transform(res, callback) {
  var entries = res.Entries || [];
  callback(null, entries.map(function (entry) {
    return {
      name: entry.Name,
      type: entry.Type,
      size: entry.Size,
      hash: entry.Hash
    };
  }));
};

module.exports = function (send) {
  return promisify(function (args, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    if (typeof args === 'function') {
      callback = args;
      opts = {};
      args = null;
    }

    return send.andTransform({
      path: 'files/ls',
      args: args,
      qs: opts
    }, transform, callback);
  });
};
