'use strict';

var promisify = require('promisify-es6');

var cleanCID = require('../utils/clean-cid');

var v = require('is-ipfs');

var bl = require('bl');

module.exports = function (send) {
  return promisify(function (hash, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    try {
      hash = cleanCID(hash);
    } catch (err) {
      if (!v.ipfsPath(hash)) {
        return callback(err);
      }
    }

    var query = {
      offset: opts.offset,
      length: opts.length
    };
    send({
      path: 'cat',
      args: hash,
      buffer: opts.buffer,
      qs: query
    }, function (err, stream) {
      if (err) {
        return callback(err);
      }

      stream.pipe(bl(function (err, data) {
        if (err) {
          return callback(err);
        }

        callback(null, data);
      }));
    });
  });
};
