'use strict';

var promisify = require('promisify-es6');

module.exports = function (send) {
  return promisify(function (name, password, callback) {
    send({
      path: 'key/export',
      args: name,
      qs: {
        password: password
      }
    }, function (err, pem) {
      if (err) return callback(err);
      callback(null, pem.toString());
    });
  });
};
