'use strict';

var promisify = require('promisify-es6');

var CID = require('cids');

module.exports = function (send) {
  return promisify(function (cid, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    if (!opts) {
      opts = {};
    }

    try {
      cid = new CID(cid);
    } catch (err) {
      return callback(err);
    }

    send({
      path: 'object/stat',
      args: cid.toString(),
      qs: opts
    }, callback);
  });
};
