'use strict';

var promisify = require('promisify-es6');

var streamToValue = require('../utils/stream-to-value');

module.exports = function (send) {
  return promisify(function (args, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    send.andTransform({
      path: 'files/read',
      args: args,
      qs: opts
    }, streamToValue, callback);
  });
};
