'use strict';

var promisify = require('promisify-es6');

var streamToValue = require('../utils/stream-to-value');

var transformChunk = require('./bw-util');

var transform = function transform(res, callback) {
  return streamToValue(res, function (err, data) {
    if (err) {
      return callback(err);
    }

    callback(null, transformChunk(data[0]));
  });
};

module.exports = function (send) {
  return promisify(function (opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    send.andTransform({
      path: 'stats/bw',
      qs: opts
    }, transform, callback);
  });
};
