'use strict';

var cleanCID = require('../utils/clean-cid');

var TarStreamToObjects = require('../utils/tar-stream-to-objects');

var v = require('is-ipfs');

var pull = require('pull-stream/pull');

var map = require('pull-stream/throughs/map');

var toPull = require('stream-to-pull-stream');

var deferred = require('pull-defer');

module.exports = function (send) {
  return function (path, opts) {
    opts = opts || {};
    var p = deferred.source();

    try {
      path = cleanCID(path);
    } catch (err) {
      if (!v.ipfsPath(path)) {
        return p.end(err);
      }
    }

    var request = {
      path: 'get',
      args: path,
      qs: opts // Convert the response stream to TarStream objects

    };
    send.andTransform(request, TarStreamToObjects, function (err, stream) {
      if (err) {
        return p.end(err);
      }

      p.resolve(pull(toPull.source(stream), map(function (file) {
        var path = file.path,
            content = file.content;
        return content ? {
          path: path,
          content: toPull.source(content)
        } : file;
      })));
    });
    return p;
  };
};
