'use strict';

var promisify = require('promisify-es6');

var CID = require('cids');

module.exports = function (send) {
  return promisify(function (cid, dLink, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    if (!opts) {
      opts = {};
    }

    try {
      cid = new CID(cid);
    } catch (err) {
      return callback(err);
    }

    var args = [cid.toString(), dLink.Name || dLink.name || null, (dLink.Hash || dLink.cid || '').toString() || null];
    send({
      path: 'object/patch/add-link',
      args: args
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      callback(null, new CID(result.Hash));
    });
  });
};
