'use strict';

var promisify = require('promisify-es6');

var streamToValueWithTransformer = require('../utils/stream-to-value-with-transformer');

var CID = require('cids');

var transform = function transform(res, callback) {
  callback(null, res.map(function (r) {
    return {
      err: r.Err ? new Error(r.Err) : null,
      cid: (r.Key || {})['/'] ? new CID(r.Key['/']) : null
    };
  }));
};

module.exports = function (send) {
  return promisify(function (opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    var request = {
      path: 'repo/gc',
      qs: opts
    };
    send(request, function (err, result) {
      if (err) {
        return callback(err);
      }

      streamToValueWithTransformer(result, transform, callback);
    });
  });
};
