'use strict';

var promisify = require('promisify-es6');

module.exports = function (send) {
  return promisify(function (args, callback) {
    if (typeof args === 'function') {
      callback = args;
      args = '/';
    }

    return send({
      path: 'files/flush',
      args: args
    }, function (error) {
      return callback(error);
    });
  });
};
