'use strict';

var moduleConfig = require('../utils/module-config');

var Stream = require('readable-stream');

var IsIpfs = require('is-ipfs');

var cleanCID = require('../utils/clean-cid');

module.exports = function (arg) {
  var send = moduleConfig(arg);
  return function (args, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    try {
      args = cleanCID(args);
    } catch (err) {
      if (!IsIpfs.ipfsPath(args)) {
        return callback(err);
      }
    }

    var pt = new Stream.PassThrough({
      objectMode: true
    });
    send({
      path: 'ls',
      args: args,
      qs: opts
    }, function (err, results) {
      if (err) {
        return callback(err);
      }

      var result = results.Objects;

      if (!result) {
        return callback(new Error('expected .Objects in results'));
      }

      result = result[0];

      if (!result) {
        return callback(new Error('expected one array in results.Objects'));
      }

      result = result.Links;

      if (!Array.isArray(result)) {
        return callback(new Error('expected one array in results.Objects[0].Links'));
      }

      result = result.map(function (link) {
        return {
          depth: 1,
          name: link.Name,
          path: args + '/' + link.Name,
          size: link.Size,
          hash: link.Hash,
          type: typeOf(link)
        };
      });
      result.forEach(function (item) {
        return pt.write(item);
      });
      pt.end();
    });
    return pt;
  };
};

function typeOf(link) {
  switch (link.Type) {
    case 1:
    case 5:
      return 'dir';

    case 2:
      return 'file';

    default:
      return 'unknown';
  }
}
