'use strict';

var Stream = require('readable-stream');

var pump = require('pump');

var transformChunk = require('./bw-util');

module.exports = function (send) {
  return function (opts) {
    opts = opts || {};
    var pt = new Stream.Transform({
      objectMode: true,
      transform: function transform(chunk, encoding, cb) {
        cb(null, transformChunk(chunk));
      }
    });
    send({
      path: 'stats/bw',
      qs: opts
    }, function (err, stream) {
      if (err) {
        return pt.destroy(err);
      }

      pump(stream, pt);
    });
    return pt;
  };
};
