'use strict';

var promisify = require('promisify-es6');

var _require = require('ipld-dag-pb'),
    DAGLink = _require.DAGLink;

var CID = require('cids');

var LRU = require('lru-cache');

var lruOptions = {
  max: 128
};
var cache = new LRU(lruOptions);

module.exports = function (send) {
  return promisify(function (cid, options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    if (!options) {
      options = {};
    }

    try {
      cid = new CID(cid);
    } catch (err) {
      return callback(err);
    }

    var node = cache.get(cid.toString());

    if (node) {
      return callback(null, node.links);
    }

    send({
      path: 'object/links',
      args: cid.toString()
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      var links = [];

      if (result.Links) {
        links = result.Links.map(function (l) {
          return new DAGLink(l.Name, l.Size, l.Hash);
        });
      }

      callback(null, links);
    });
  });
};
