'use strict';

var promisify = require('promisify-es6');

var findSources = require('../utils/find-sources');

module.exports = function (send) {
  return promisify(function () {
    var _findSources = findSources(Array.prototype.slice.call(arguments)),
        callback = _findSources.callback,
        sources = _findSources.sources,
        opts = _findSources.opts;

    send({
      path: 'files/cp',
      args: sources,
      qs: opts
    }, function (error) {
      return callback(error);
    });
  });
};
