import React, { Component } from 'react';
import Web3 from 'web3';
import Identicon from 'identicon.js';
import './App.css';
import Decentragram from '../abis/Decentragram.json'
import Navbar from './Navbar'
import Main from './Main'

const ipfsClient = require("ipfs-http-client")
const ipfs = ipfsClient({host: 'ipfs.infura.io', port: 5001, protocol: 'https'})


class App extends Component {

  async componentWillMount(){
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3(){

    if (window.ethereum){
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3){
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else{
      window.alert('No ethereum browser found. You should add metamask extension to your browser.')
    }
  }

  async loadBlockchainData(){
    const web3 = window.web3

    //load acount
    const accounts = await web3.eth.getAccounts()
    this.setState({account: accounts[0]})

    const networkId =await web3.eth.getId()
    const networkData = Decentragram.networks[networkId]

    if (networkData){
      const decentragram = web3.eth.Contract(Decentragram.abi, networkData.address)
      this.setState({decentragram: decentragram})
      const imagesCount = await decentragram.methods.imageCount().call()
      this.setState({imagesCount})

      this.setState({loading: false})

    } else{
      window.alert("Decentragram contract is not deployed to detected network.")


    }
  }

  captureFile = event =>{
    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)

    reader.onloadend = () =>{
      this.setState({buffer: Buffer(reader.result)})
      console.log('buffer', this.state.buffer)
    }
  }

  uploadImage = description =>{

    console.log("Submiting the file to IPFS...")

    //adding the file to ipfs
    ipfs.add(this.state.buffer, (error,result) => {
      console.log('IPFS result', result)
      if(error) {
        console.error(error)
        return
      }

      //this.setState({loading:true})
      //this.state.decentragram.methods.uploadImage(result[0].hash, description).send({from: this.state.account}).on('transactionHash', (hash) =>)
    })
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      decentragram: null,
      images: [],
      loading: true
    }
  }

  render() {
    return (
      <div>
        <Navbar account={this.state.account} />
        { this.state.loading
          ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
          : <Main
              captureFile={this.captureFile}
            />
          }
        }
      </div>
    );
  }
}

export default App;
