'use strict';

var promisify = require('promisify-es6');

var _require = require('ipld-dag-pb'),
    DAGNode = _require.DAGNode,
    DAGLink = _require.DAGLink;

var CID = require('cids');

var LRU = require('lru-cache');

var lruOptions = {
  max: 128
};
var cache = new LRU(lruOptions);

module.exports = function (send) {
  return promisify(function (cid, options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    if (!options) {
      options = {};
    }

    var cidB58Str;

    try {
      cid = new CID(cid);
      cidB58Str = cid.toBaseEncodedString();
    } catch (err) {
      return callback(err);
    }

    var node = cache.get(cidB58Str);

    if (node) {
      return callback(null, node);
    }

    send({
      path: 'object/get',
      args: cidB58Str,
      qs: {
        'data-encoding': 'base64'
      }
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      var links = result.Links.map(function (l) {
        return new DAGLink(l.Name, l.Size, l.Hash);
      });
      var node = DAGNode.create(Buffer.from(result.Data, 'base64'), links);
      cache.set(cidB58Str, node);
      callback(null, node);
    });
  });
};
