var promisify = require('promisify-es6');

var Block = require('ipfs-block');

var CID = require('cids');

var streamToValue = require('../utils/stream-to-value');

module.exports = function (send) {
  return promisify(function (args, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    } // TODO this needs to be adjusted with the new go-ipfs http-api


    var cid;

    try {
      if (CID.isCID(args)) {
        cid = args;
        args = cid.toBaseEncodedString();
      } else if (Buffer.isBuffer(args)) {
        cid = new CID(args);
        args = cid.toBaseEncodedString();
      } else if (typeof args === 'string') {
        cid = new CID(args);
      } else {
        return callback(new Error('invalid argument'));
      }
    } catch (err) {
      return callback(err);
    } // Transform the response from Buffer or a Stream to a Block


    var transform = function transform(res, callback) {
      if (Buffer.isBuffer(res)) {
        callback(null, new Block(res, cid)); // For empty blocks, concat-stream can't infer the encoding so we are
        // passed back an empty array
      } else if (Array.isArray(res) && res.length === 0) {
        callback(null, new Block(Buffer.alloc(0), cid));
      } else {
        streamToValue(res, function (err, data) {
          if (err) {
            return callback(err);
          } // For empty blocks, concat-stream can't infer the encoding so we are
