'use strict';

var promisify = require('promisify-es6');

var mapKeys = require('just-map-keys');

var kebabCase = require('just-kebab-case');

var transform = function transform(data, callback) {
  callback(null, {
    type: data.Type,
    blocks: data.Blocks,
    size: data.Size,
    hash: data.Hash,
    cumulativeSize: data.CumulativeSize,
    withLocality: data.WithLocality || false,
    local: data.Local || undefined,
    sizeLocal: data.SizeLocal || undefined
  });
};

module.exports = function (send) {
  return promisify(function (args, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    opts = mapKeys(opts, function (v, k) {
      return kebabCase(k);
    });
    send.andTransform({
      path: 'files/stat',
      args: args,
      qs: opts
    }, transform, callback);
  });
};
