'use strict';

var promisify = require('promisify-es6');

module.exports = function (send) {
  return promisify(function (hash, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = null;
    }

    send({
      path: 'pin/rm',
      args: hash,
      qs: opts
    }, function (err, res) {
      if (err) {
        return callback(err);
      }

      callback(null, res.Pins.map(function (hash) {
        return {
          hash: hash
        };
      }));
    });
  });
};
