'use strict';

var pull = require('pull-stream');

var toPull = require('stream-to-pull-stream');

var deferred = require('pull-defer');

var moduleConfig = require('../utils/module-config');

module.exports = function (send) {
  send = moduleConfig(send);
  return function (opts) {
    opts = opts || {};
    var p = deferred.source();
    send({
      path: 'refs/local',
      qs: opts
    }, function (err, stream) {
      if (err) {
        return p.resolve(pull.error(err));
      }

      p.resolve(pull(toPull.source(stream), pull.map(function (r) {
        return {
          ref: r.Ref,
          err: r.Err
        };
      })));
    });
    return p;
  };
};
